  #log{height: 56vh; overflow:auto; padding: 14px;}

  .msg{display:flex; gap:10px; margin: 10px 0; align-items:flex-start;}
  .who{flex: 0 0 auto; font-weight: 700; font-size: 12px; color: var(--muted); margin-top: 2px;}
  .bubble{
    flex: 1 1 auto; padding: 10px 12px; border-radius: 14px;
