  #actionsList, #historyList{display:flex; flex-direction:column; gap:10px;}

  .card{
    border:1px solid rgba(255,255,255,08);
    border-radius: 14px;
    padding: 12px;
    background: rgba(0,0,0,20);
  }
  .card.subtle{ background: rgba(0,0,0,14); }

  .card h4{
    margin:0 0 8px 0;
    font-size: 14px;
    display:flex;
    align-items:center;
    justify-content:space-between;
    gap:10px;
  }

  .kv{display:flex; flex-wrap:wrap; gap:10px; color: var(--muted); font-size: 12px;}

  details summary{cursor:pointer; color: var(--accent); margin-top: 10px; user-select:none;}
  details pre{
    white-space: pre-wrap;
    border:1px solid rgba(255,255,255,07);
    border-radius: 12px;
    padding: 10px;
    overflow:auto;
    margin-top: 8px;
    color: var(--text);
  }

  .btns{display:flex; gap:10px; flex-wrap:wrap; margin-top: 10px;}
  .previewLink{
    display:inline-flex;
    align-items:center;
    gap:8px;
    padding: 12px 14px;
    border-radius: 12px;
    border: 1px solid rgba(255,255,255,10);
    color: var(--text);
    text-decoration:none;
    cursor:pointer;
    transition: transform .04s ease, background .12s ease, border-color .12s ease;
  }
  .previewLink:hover{ border-color: rgba(255,255,255,16); text-decoration:none;}
  .previewLink:active{ transform: translateY(1px); }
  .previewDisabled{ font-size: 12px; color: var(--muted2); padding: 10px 0; }

  .badge{
    display:inline-block;
    font-size: 12px;
    padding: 3px 9px;
    border-radius: 999px;
    border: 1px solid rgba(255,255,255,10);
    color: var(--muted);
    text-transform: lowercase;
    white-space: nowrap;
  }
  .badge.pending{ color: #ffd08a; border-color: rgba(251,191,36,25); background: rgba(251,191,36,08); }
  .badge.running{ color: #9cc6ff; border-color: rgba(106,166,255,25); background: rgba(106,166,255,08); }
  .badge.done{ color: #86f8e8; border-color: rgba(45,212,191,25); background: rgba(45,212,191,08); }
  .badge.failed{ color: #ff9aa9; border-color: rgba(251,113,133,25); background: rgba(251,113,133,08); }
  .badge.rejected, .badge.rolled_back{ color: #c7cfdd; border-color: rgba(255,255,255,10); }

  /* Collapsible Recent Actions */
  .sectionSummary{
    display:flex;
    align-items:center;
    justify-content:space-between;
    cursor:pointer;
    user-select:none;
    padding:6px 2px;
  }

  #history details{
    border:1px solid var(--border, #2a2a2a);
    border-radius:14px;
    padding:10px;
  }

  #history details[open] .sectionSummary{
    margin-bottom:10px;
  }

  /* Contain history so it doesn’t stretch the page */
  #historyList{
    max-height: 34vh;
    overflow: auto;
    padding-right: 6px;
  }

